package date0809;

public class test3 {
        public static void main(String[] args) {
            System.out.println(addComma("-11111100"));
            System.out.println(addComma("-11.23344455"));
            System.out.println(addComma("51711111234"));
            System.out.println(addComma(".111"));
        }

    private static String addComma(String input) {
        boolean isMinus = input.contains("-");
        boolean isDecimal = input.contains(".");
        String integerPart;
        String decimalPart = "";

        if (isDecimal) {
            String[] list = input.split("\\.");
            integerPart = list[0];
            decimalPart = list[1];
        } else {
            integerPart = input;
        }

        for (int i = integerPart.length()-3 ; i > 0; i = i-3) {
            if (isMinus && i == 1) break;
            integerPart = integerPart.substring(0, i) + "," + integerPart.substring(i);
        }
        if (isDecimal) {
            return integerPart + "." + decimalPart;
        } else {
            return integerPart;
        }
    }
}
